import React, { useState, useEffect, useCallback } from 'react';
import axios from 'axios';
import './TaskForm.css';
import EditTaskPopup from './EditTaskPopup';
import './EditTaskPopup.css';

const TaskForm = ({ onCreate, onUpdate, onDelete, onCancel }) => {
  const [teamMembers, setTeamMembers] = useState([]);
  const [selectedTeamMember, setSelectedTeamMember] = useState('');
  const [description, setDescription] = useState('');
  const [status, setStatus] = useState('');
  const [createdDate, setCreatedDate] = useState('');
  const [updatedDate, setUpdatedDate] = useState('');
  const [formErrors, setFormErrors] = useState({ teamMember: '', description: '', status: '', createdDate: '', updatedDate: '' });
  const [successMessage, setSuccessMessage] = useState('');
  const [errorMessage, setErrorMessage] = useState('');

  const fetchTeamMembers = useCallback(async () => {
    try {
      const response = await axios.get('http://localhost:1725/api/v1/team-members/get-all');
      setTeamMembers(response.data.data);
    } catch (error) {
      console.error('Error fetching team members:', error);
    }
  }, []);

  useEffect(() => {
    fetchTeamMembers();
  }, [fetchTeamMembers]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (validateForm()) {
      const taskData = {
        teamMembers: {
          id: selectedTeamMember
        },
        description,
        status,
        createdDate,
        updatedDate
      };

      try {
        await axios.post('http://localhost:1725/api/v1/task', taskData);
        setSuccessMessage('Your Task added successfully');
        onCreate();
      } catch (error) {
        setErrorMessage('Error adding task');
        console.error('Error creating task:', error);
      }

      setDescription('');
      setStatus('');
      setCreatedDate('');
      setUpdatedDate('');
    }
  };

  const validateForm = () => {
    let errors = { teamMember: '', description: '', status: '', createdDate: '', updatedDate: '' };
    let isValid = true;

    if (!selectedTeamMember) {
      errors.teamMember = 'Please select a team member';
      isValid = false;
    }

    if (!description.trim()) {
      errors.description = 'Description is required';
      isValid = false;
    }

    if (!status.trim()) {
      errors.status = 'Please select a status';
      isValid = false;
    }

    if (!createdDate.trim()) {
      errors.createdDate = 'Created Date is required';
      isValid = false;
    }

    if (!updatedDate.trim()) {
      errors.updatedDate = 'Updated Date is required';
      isValid = false;
    }

    setFormErrors(errors);
    return isValid;
  };

  return (
    <div className="task-form-container">
      {successMessage && <div className="success-message">{successMessage}</div>}
      {errorMessage && <div className="error-message">{errorMessage}</div>}
      <h2 className="task-form-title">Add Task</h2>
      <form onSubmit={handleSubmit} className="task-form">
        <div className="form-group">
          <label className="form-label">Team Member:</label>
          <select value={selectedTeamMember} onChange={(e) => setSelectedTeamMember(e.target.value)} className="form-select">
            <option value="">Select Team Member</option>
            {teamMembers.map(member => (
              <option key={member.id} value={member.id}>{member.name}</option>
            ))}
          </select>
          {formErrors.teamMember && <span className="error">{formErrors.teamMember}</span>}
        </div>

        <div className="form-group">
          <label className="form-label">Description:</label>
          <textarea value={description} onChange={(e) => setDescription(e.target.value)} className="form-textarea" />
          {formErrors.description && <span className="error">{formErrors.description}</span>}
        </div>

        <div className="form-group">
          <label className="form-label">Status:</label>
          <select value={status} onChange={(e) => setStatus(e.target.value)} className="form-select">
            <option value="">Please select</option>
            <option value="PENDING">Pending</option>
            <option value="IN_PROGRESS">In Progress</option>
            <option value="COMPLETED">Completed</option>
          </select>
          {formErrors.status && <span className="error">{formErrors.status}</span>}
        </div>

        <div className="form-group">
          <label className="form-label">Created Date:</label>
          <input type="date" value={createdDate} onChange={(e) => setCreatedDate(e.target.value)} className="form-input" />
          {formErrors.createdDate && <span className="error">{formErrors.createdDate}</span>}
        </div>

        <div className="form-group">
          <label className="form-label">Updated Date:</label>
          <input type="date" value={updatedDate} onChange={(e) => setUpdatedDate(e.target.value)} className="form-input" />
          {formErrors.updatedDate && <span className="error">{formErrors.updatedDate}</span>}
        </div>

        <div className="form-buttons">
          <button type="submit" className="btn btn-primary">Add Task</button>
          <button type="button" onClick={onCancel} className="btn btn-secondary">Cancel</button>
        </div>
      </form>
    </div>
  );
};

export default TaskForm;
